 
 import UIKit
 
 // サイズ計算
 func size(a : NSMutableAttributedString) -> CGRect{
    return a.boundingRectWithSize(CGSize(width: 200, height: CGFloat.max), options: NSStringDrawingOptions.UsesLineFragmentOrigin, context: nil)
 }
 
 func resize(img : UIImage, width: Int) -> UIImage{
    let height = CGFloat(width) * (img.size.height / img.size.width)
    let rect = CGRectMake(0, 0, CGFloat(width), height)
    UIGraphicsBeginImageContext(rect.size)
    img.drawInRect(rect)
    let newImg = UIGraphicsGetImageFromCurrentImageContext()
    UIGraphicsEndImageContext()
    return newImg
 }
 
 func imgAtStr(url : String, width: Int = 300) -> NSAttributedString{
    let url = NSURL(string: url)
    let req = NSURLRequest(URL: url)
    var res : AutoreleasingUnsafeMutablePointer<NSURLResponse?> = nil
    var err : NSError
    let d : NSData? = NSURLConnection.sendSynchronousRequest(req, returningResponse: res, error: nil)
    
    let img = UIImage(data: d!)
    
    var at = NSTextAttachment()
    at.image = resize(img,width)
    return NSAttributedString(attachment: at)
 }
 
